import React,{useState,useEffect} from 'react'

const UseEffects2 = () => {
    const [width, setWidth] = useState(windowwidth);
    useEffect(() => {
        setWidth(window.innerWidth)
    }, [width])
    return (
        <div>
            <h2>My window size is {width}</h2>
        </div>
    )
}

export default UseEffects2
