import React,{use} from "react";

function UseStateHook() {
  let val = "tahidur";
  const valueChange = () => (val = "tahid");
  return (
    <div>
      <h2>{val}</h2>
      <button onChange={valueChange}>Click me !</button>
    </div>
  );
}

export default UseStateHook;
